pub í´ë˜ìŠ¤ BuyTicket {
    _dataRepository: ports.ForStoringData;
    _paymentService: ports.ForPaying;
    _dateTimeService: ports.ForObtainingDateTime;

    ìƒˆë¡œìš´(
        dataRepository: ports.ForStoringData,
